function getRecommendation() {
    const vehicleType = document.getElementById('vehicle-type').value;
    const brandSelect = document.getElementById('brand-select');
    const brandLabel = document.getElementById('brand-label');
    const showVehiclesButton = document.getElementById('show-vehicles-button');
    const recommendationDiv = document.getElementById('recommendation');
    

    const items = recommendations[vehicleType] || [];
    const brands = [...new Set(items.map(item => item.brand))]; // Unique brands

    // Populate brand select
    brandSelect.innerHTML = brands.map(brand => `<option value="${brand}">${brand}</option>`).join('');
    brandSelect.style.display = 'block';
    brandLabel.style.display = 'block';
    showVehiclesButton.style.display = 'block';
    
    // Display all vehicles initially
    recommendationDiv.innerHTML = items.map(item => `
        <div class="recommendation-item">
            <a href="isi.html?name=${encodeURIComponent(item.name)}&image=${encodeURIComponent(item.image)}&description=${encodeURIComponent(item.description)}&pros=${encodeURIComponent(JSON.stringify(item.pros))}&cons=${encodeURIComponent(JSON.stringify(item.cons))}&price=${encodeURIComponent(item.price)}&reviews=${encodeURIComponent(JSON.stringify(item.reviews))}">
                <img src="${item.image}" alt="${item.name}">
                <div>
                    <h3>${item.name}</h3>
                    <p>${item.description}</p>
                </div>
            </a>
        </div>
    `).join('');
}

function showVehicles() {
    const vehicleType = document.getElementById('vehicle-type').value;
    const selectedBrand = document.getElementById('brand-select').value;
    const recommendationDiv = document.getElementById('recommendation');

    const items = recommendations[vehicleType].filter(item => item.brand === selectedBrand);
    recommendationDiv.innerHTML = items.map(item => `
        <div class="recommendation-item">
            <a href="isi.html?name=${encodeURIComponent(item.name)}&image=${encodeURIComponent(item.image)}&description=${encodeURIComponent(item.description)}&pros=${encodeURIComponent(JSON.stringify(item.pros))}&cons=${encodeURIComponent(JSON.stringify(item.cons))}&price=${encodeURIComponent(item.price)}&reviews=${encodeURIComponent(JSON.stringify(item.reviews))}">
                <img src="${item.image}" alt="${item.name}">
                <div>
                    <h3>${item.name}</h3>
                    <p>${item.description}</p>
                </div>
            </a>
        </div>
    `).join('');
}
